<?php    $total = 0;?><table class="table table-bordered">    <thead>        <tr>            <th>ชื่อพนักงาน</th>            <th>ที่เก็บ</th>            <th>Factor</th>            <th>จำนวนเงิน</th>                     </tr>    </thead>    <tbody>        <?php        foreach($output as $k=> $v):?>        <tr>            <td><?= $v['customer']?></td>            <td><?= $v['treasury']?></td>            <td>X <?= $v['factor']?></td>            <td><?php                    $price = isset($v['com'])?$v['com']:0;                    echo number_format($price, 2);                    $total += $price;                ?></td>                    </tr>        <?php endforeach;?>    </tbody>    <tfoot>        <tr>            <td colspan="3">รวมเงินทั้งหมด</td>            <td><b><?= number_format($total, 2); ?></b> บาท</td>                    </tr>    </tfoot></table>