<div class="kt-portlet ">        <div class="kt-portlet__body">        <div class="kt-widget4">                        <?php if($model):?>                <?php if($model['driver']):?>                    <?php foreach ($model['driver'] as $k=>$v):?>                        <div class="kt-widget4__item">                            <span class="kt-widget4__icon">&nbsp;<i class="fa fa-user"></i>&nbsp;</span>                             <a class="kt-widget4__title kt-widget4__title--light" href="#" data-target="#<?= $v['id']?>">                                <?= $v['userName']; ?> <label>คลัง: <?= $v['treasury']; ?> ที่เก็บข้อมูล: <?= $v['storage']; ?></label>                            </a>                            <span class="kt-widget4__number kt-font-info">                                <?php if(Yii::$app->user->can('manage_user_percent')):?>                                    <button data-url="<?= \yii\helpers\Url::to(['/product-list/delete?id='.$v['id']])?>" data-id="<?= $v['id']?>" class="btn btn-xs btn-danger btn-del-user-package-parent btn-outline-danger">ลบ</button>                                <?php endif; ?>                            </span>                        </div>                    <?php endforeach;?>            <?php endif;?>        <?php endif; ?>                                                 </div>    </div></div>                                           <?php \richardfan\widget\JSRegister::begin();?><script>    $(".btn-user-shipping-parent").on('click',function () {        let url = $(this).attr('data-url');        modalUserShipping(url);        return false;    });    $(".btn-del-user-package-parent").on('click',function () {        let url = $(this).attr('data-url');        bootbox.confirm({            message: "คุณต้องการลบรายหรือไม่",            buttons: {                confirm: {                    label: 'Yes',                    className: 'btn-success'                },                cancel: {                    label: 'No',                    className: 'btn-danger'                }            },            callback: function (result) {                if(result){                    $.get(url,function (result) {                        if(result.status == 'success') {                            PreviewUserShipping();                            initUserCal2();                        }                    });                }            }        });        return false;    });    function modalUserShipping(url) {        $('.modal').css('overflow', 'scroll');        $('#modal-user-shipping .modal-content').html('<div class=\"sdloader \"><i class=\"sdloader-icon\"></i></div>');        $('#modal-user-shipping').modal('show')            .find('.modal-content')            .load(url);    }    function PreviewUserShipping(){        let url = '<?= \yii\helpers\Url::to(['/product-list/init-preview-user-shipping?type=99'])?>';        $.get(url, function (result) {            $("#preview-user-package").html(result);        });    }</script><?php \richardfan\widget\JSRegister::end();?></div>