<div class="kt-portlet ">        <div class="kt-portlet__body">        <div class="kt-widget4">                        <?php if($model):?>                <?php if($model['driver']):?>                    <?php foreach ($model['driver'] as $k=>$v):?>                        <div class="kt-widget4__item">                            <span class="kt-widget4__icon">&nbsp;<i class="fa fa-user"></i>&nbsp;</span>                             <a class="kt-widget4__title kt-widget4__title--light" href="#" data-target="#<?= $v['id']?>">คนขับ: <?= $v['userName']; ?> </a>                             <span class="kt-widget4__number kt-font-info">                                <?php if(Yii::$app->user->can('manage_user_percent')):?>                                    <a href="#" class="btn btn-xs btn-success btn-user-shipping-parent btn-outline-success" data-url="<?= \yii\helpers\Url::to(['/product-list/add-driver?type=2&parent_id='.$v['id']])?>"><i class="fa fa-plus"></i> เพิ่มลูกน้อง</a>                                    <button data-url="<?= \yii\helpers\Url::to(['/product-list/delete?id='.$v['id']])?>" data-id="<?= $v['id']?>" class="btn btn-xs btn-danger btn-del-user-shipping-parent btn-outline-danger">ลบ</button>                                <?php endif; ?>                            </span>                        </div>                         <!-- ลูกน้อง -->                        <div id="<?= $v['id']?>">                                <?php if(isset($v['parent'])):?>                                    <?php foreach ($v['parent'] as $k2=>$v2):?>                                        <div>                                            <div class="kt-widget4__item" id="<?= $v['id']?>">                                                <span class="kt-widget4__icon">&nbsp;<i class="fa fa-user  kt-font-success"></i>&nbsp;</span>                                                 <a class="kt-widget4__title kt-widget4__title--light" href="#"> ลูกน้อง: <?= $v2['userName'];?></a>                                                 <span class="kt-widget4__number kt-font-success">                                                    <?php if(Yii::$app->user->can('manage_user_percent')):?>                                                    <button title="ลบ" data-url="<?= \yii\helpers\Url::to(['/product-list/delete?id='.$v2['id']])?>" data-id="<?= $v2['id']?>" class="btn btn-xs btn-danger btn-del-user-shipping-parent btn-outline-danger"><i class="fa fa-times"></i></button>                                                    <?php endif; ?>                                                </span>                                            </div>                                         </div>                                    <?php endforeach;?>                                <?php endif;?>                        </div>                     <?php endforeach;?>            <?php endif;?>        <?php endif; ?>                                                 </div>    </div></div>                                           <?php \richardfan\widget\JSRegister::begin();?><script>    $(".btn-user-shipping-parent").on('click',function () {        let url = $(this).attr('data-url');        modalUserShipping(url);        return false;    });    $(".btn-del-user-shipping-parent").on('click',function () {        let url = $(this).attr('data-url');        bootbox.confirm({            message: "คุณต้องการลบรายหรือไม่",            buttons: {                confirm: {                    label: 'Yes',                    className: 'btn-success'                },                cancel: {                    label: 'No',                    className: 'btn-danger'                }            },            callback: function (result) {                if(result){                    $.get(url,function (result) {                        if(result.status == 'success') {                            PreviewUserShipping();                            initUserCal();                        }                    });                }            }        });        return false;    });    function modalUserShipping(url) {        $('.modal').css('overflow', 'scroll');        $('#modal-user-shipping .modal-content').html('<div class=\"sdloader \"><i class=\"sdloader-icon\"></i></div>');        $('#modal-user-shipping').modal('show')            .find('.modal-content')            .load(url);    }    function PreviewUserShipping(){        let url = '<?= \yii\helpers\Url::to(['/product-list/init-preview-user-shipping'])?>';        $.get(url, function (result) {            $("#preview-user-shipping").html(result);        });    }</script><?php \richardfan\widget\JSRegister::end();?></div>