<form  id="form-search">    <div class="alert alert-default" style="background: white;border: 3px solid #4abdac;border-radius: 5px;">        <div class="row">            <div class="col-md-3 col-xs-6">                <?php                    $stdate = Yii::$app->request->get('stdate');                    use kartik\date\DatePicker;                    echo '<label class="control-label">วันที่เริ่มต้น</label>';                    echo DatePicker::widget([                        'name' => 'stdate',                        'type' => DatePicker::TYPE_COMPONENT_PREPEND,                        'value' => isset($stdate)?$stdate:date('d-m-Y'),//Y-m-d                        'options' => ['id' => 'stdate'],                        'pluginOptions' => [                            'autoclose' => true,                            'format' => 'dd-mm-yyyy'//'yyyy-mm-dd'                        ]                    ]);                ?>            </div>            <div class="col-md-3 col-xs-6">                <?php                echo '<label class="control-label">ถึงวันที่</label>';                $endate = Yii::$app->request->get('endate');                echo DatePicker::widget([                    'name' => 'endate',                    'type' => DatePicker::TYPE_COMPONENT_APPEND,                    'value' => isset($endate)?$endate:date('d-m-Y'),//Y-m-d                    'options' => ['id' => 'endate'],                    'pluginOptions' => [                        //'orientation' => 'top right',                        'autoclose' => true,                        'format' => 'dd-mm-yyyy'//'yyyy-mm-dd'                    ]                ]);                ?>            </div>            <div class="col-md-3 col-xs-6" >                <label>ชื่อลูกค้า</label>                <?php                    $customer_value = Yii::$app->request->get('customer','AR');                    echo \yii\helpers\Html::textInput('customer',$customer_value,['class'=>'form-control']);                ?>            </div>        </div>        <div class="row" style="margin-top:20px">            <div class="col-md-3 col-xs-6" >                <label>สถานะการใช้งานบิล</label>                <?php                    $rstat_value = Yii::$app->request->get('rstat');                    $items = [1=>'เปิดการใช้งานบิล',0=>'ปิดการใช้งานบิล','2'=>'ไม่สามารถแก้ไขบิลได้'];;                    echo \yii\bootstrap\Html::dropDownList('rstat',$rstat_value,$items,[                        'class'=>'form-control',                        'prompt'=>'--เลือกสถานะบิล--'                    ]);                ?>            </div>            <div class="col-md-3 col-xs-6" >                <label>สถานะบิล</label>                <?php                    $bill_status_value = Yii::$app->request->get('bill_status');                    $bill_type = \backend\models\BillStatus::find()->where('rstat not in(0,3)')->all();                    $items = \yii\helpers\ArrayHelper::map($bill_type,'id','name');                    echo \yii\bootstrap\Html::dropDownList('bill_status',$bill_status_value,$items,[                        'class'=>'form-control',                        'prompt'=>'--เลือกสถานะบิล--'                    ]);                ?>            </div>            <div class="col-md-3 col-xs-6" >                <label>สถานะการส่งสินค้า</label>                <?php                    $shiping_value = Yii::$app->request->get('shiping');                    $items = \yii\helpers\ArrayHelper::map(\backend\models\BillStatusShipping::find()->where('rstat not in(0,3)')->all(), 'id', 'name');                    echo \yii\bootstrap\Html::dropDownList('shiping',$shiping_value,$items,[                        'class'=>'form-control',                        'prompt'=>'--เลือกสถานะจัดส่ง--'                    ]);                ?>            </div>            <div class="col-md-3 col-xs-6" >                <label>สถานะเก็บเงิน</label>                <?php                    $charge_value = Yii::$app->request->get('charge');                    $items = \yii\helpers\ArrayHelper::map(\backend\models\BillStatusCharge::find()->where('rstat not in(0,3)')->all(), 'id', 'name');                    echo \yii\bootstrap\Html::dropDownList('charge',$charge_value,$items,[                        'class'=>'form-control',                        'prompt'=>'--เลือกสถานะเก็บเงิน--'                    ]);                ?>            </div>        </div>        <div class="row">            <div class="col-md-12" style="margin-top:25px;">                <button class="btn btn-primary" id="btnProcess">ค้นหา</button>            </div>        </div>    </div></form>