<?phpuse appxq\sdii\widgets\ModalForm; ?><div>    <h3>กล่องที่ <?= isset($blog->name)?$blog->name:'';?></h3>    <br></div><table class="table table-responsive table-bordered table-hover">    <thead>    <tr>        <th width="60">ลำดับที่</th>        <th>หมายเลขบิล</th>        <th>สถานะ</th>        <th></th>    </tr>    </thead>    <tbody>    <?php foreach($billno as $k=>$v){?>            <tr>                <td class="text-center"><?= $k+1; ?></td>                <td><?= $v; ?></td>                <td>                    <?php                         $bill = \backend\models\BillItems::find()->where(['billno'=>$v])->one();                         if($bill){                            $btype = isset($bill->billtypes->name)?$bill->billtypes->name:'';//ประเภทบิล                            $bname = isset($bill['billref'])?$bill['billref']:''; //ชื่อบิล                            $billno = isset($bill['billno'])?$bill['billno']:''; //หมายเลขบิล                            $billfullname  = "{$btype}{$bname}";                             echo '                                <div style="padding:5px;" class="kt-border-success kt-section section-sm kt-portlet__body body-xs kt-font-success content-border">                                            <div class="row">                                                <div class="col-md-8 col-xs-8 col-sm-8">'.$billno.'</div>                                                <div class="col-md-4 col-xs-4 col-sm-4 text-right">                                                    '.$billfullname.'                                                </div>                                            </div>                                        </div>                             ';                         }else{                             echo '                                <div style="padding:5px;" class="kt-border-danger kt-section section-sm kt-portlet__body body-xs kt-font-danger content-border">                                            <div class="row">                                                <div class="col-md-8 col-xs-8 col-sm-8">ไม่พบบิล</div>                                                <div class="col-md-4 col-xs-4 col-sm-4 text-right">                                                  <button class="btn btn-success btnCreate" data-id="'.$v.'"><i class="fa fa-plus"></i> เพิ่มบิล</button>                                                </div>                                            </div>                                        </div>                             ';                         }                    ?>                </td>            </tr>    <?php }?>    </tbody></table><?=ModalForm::widget([    'id' => 'modal-bill-items',    'options' => ['tabindex' => false],    'size' => 'modal-lg',    //         'clientOptions' => ['backdrop' => 'static', 'keyboard' => false]]);?><?php \richardfan\widget\JSRegister::begin(); ?><script>    $(".btnCreate").on('click', function(){        let billno = $(this).attr('data-id');        let blog = '<?= $block; ?>';        let url = '<?= \yii\helpers\Url::to(['/bill-items/create?billno='])?>'+billno+'&blog='+blog;        modalBillItem(url);        return false;    });    function modalBillItem(url) {        $('.modal').css('overflow', 'scroll');        $('#modal-bill-items .modal-content').html('<div class=\"sdloader \"><i class=\"sdloader-icon\"></i></div>');        $('#modal-bill-items').modal('show')            .find('.modal-content')            .load(url);    }</script><?php \richardfan\widget\JSRegister::end(); ?>