<div class="row">    <div class="col-md-12">        <select class="form-control" id="btnSelectBlock">            <option>เลือกกล่อง</option>            <?php foreach ($block as $k=>$v):?>                <option value="<?= $v->id; ?>"><?= $v->name; ?></option>            <?php endforeach; ?>        </select>        <br>        <div id="show-report" style="background: #fff;"><h3 class="text-center">ไม่พบข้อมูล กรุณาเลือกกล่องเพื่อค้นหาข้อมูลบิล</h3></div>    </div></div><?php \richardfan\widget\JSRegister::begin(); ?><script>    $("#btnSelectBlock").on('change', function(){        let val = $(this).val();        let url = '<?= \yii\helpers\Url::to(['/report/block-data'])?>';        $.post(url,{block:val},function(result){            $("#show-report").html(result);        });        return false;    });</script><?php \richardfan\widget\JSRegister::end(); ?>