<div class="row">    <div class="col-md-12">        <label for="">เลือกกล่อง</label>        <select class="form-control" id="btnSelectBlock">            <option>เลือกกล่อง</option>            <?php foreach ($block as $k=>$v):?>                <option value="<?= $v->id; ?>"><?= $v->name; ?></option>            <?php endforeach; ?>        </select>        <br>        <div id="show-report" style="background: #fff;padding: 10px;"><h3 class="text-center">ไม่พบข้อมูล กรุณาเลือกกล่องเพื่อค้นหาข้อมูลบิล</h3></div>    </div></div><?php if(isset(\Yii::$app->session['block'])):?>    <?php        $blockid = isset(\Yii::$app->session['block'])?\Yii::$app->session['block']:'';        //\appxq\sdii\utils\VarDumper::dump($blockid);    ?>    <?php \richardfan\widget\JSRegister::begin(); ?>        <script>            $('#btnSelectBlock option[value="<?= $blockid; ?>"]').attr("selected",true);            $("#show-report").html("<h1 class='text-center'>กำลังโหลดข้อมูล...</h1>");            loadData('<?= $blockid; ?>');        </script>        <?php \richardfan\widget\JSRegister::end(); ?><?php endif; ?><?php \richardfan\widget\JSRegister::begin(); ?><script>    $("#btnSelectBlock").on('change', function(){        let val = $(this).val();        $("#show-report").html("<h1 class='text-center'>กำลังโหลดข้อมูล...</h1>");        loadData(val);    });    function loadData(data){        let url = '<?= \yii\helpers\Url::to(['/report/block-data'])?>';        $.post(url,{block:data},function(result){            $("#show-report").html(result);        });        return false;    }</script><?php \richardfan\widget\JSRegister::end(); ?>