<?php    $this->title = 'ค่า Commission';?><div>    <input type="date" name="stdate" id="stdate">    <input type="date" name="endate" id="endate">    <button class="btn btn-primary" id="btnProcess">แสดงค่าคอม</button></div><div id="showReport"></div><?php \richardfan\widget\JSRegister::begin()?><script>    $("#btnProcess").on('click', function () {       let stdate = $("#stdate").val();       let endate = $("#endate").val();       let url = '<?= \yii\helpers\Url::to(['/report/customer-car-data'])?>';       $.post(url,{stdate:stdate, endate:endate}, function(res){          $("#showReport").html(res)       });       return false;    });</script><?php \richardfan\widget\JSRegister::end()?>