et endate = $("#endate").val();       let url = '<?= \yii\helpers\Url::to(['/report/customer-car-data'])?>';       $.post(url,{stdate:stdate, endate:endate}, function(res){          $("#showReport").html(res)       });       return false;    });</script><?php \richardfan\widget\JSRegister::end()?>