<div style="background: #fff;">    <table class="table table-bordered table-responsive">        <thead style="background: #4abdac;    color: #fff;">        <tr>            <th width="50">ลำดับ</th>            <th>ชื่อบิล</th>            <th>ชื่อพนักงาน</th>            <th>ตำแหน่ง</th>            <th>ที่เก็บ</th>            <th>Factor</th>            <th>Percent ที่ได้</th>            <th>มูลค่า/บาท</th>            <th>วันที</th>        </tr>        </thead>        <tbody>        <?php            $totalPrice=0;        ?>        <?php foreach($data as $k=>$v):?>            <tr>                <td class="text-center"><?= $k+1; ?></td>                <td>                    <?php                        $bill = \backend\models\BillItems::findOne($v['bill_id']);                        echo $bill->billref;                    ?>                </td>                <td><?= $v['driver']; ?></td>                <td><?= $v['position']; ?></td>                <td class="text-right"><?= $v['treasury']; ?></td>                <td class="text-right"><?= $v['factor']; ?></td>                <td class="text-right"><?= $v['percent_package'];?></td>                <td class="text-right"><?php $totalPrice+=$v['price']; echo number_format($v['price'], 2); ?></td>                <td><?= isset($v['create_date'])?\appxq\sdii\utils\SDdate::mysql2phpDate($v['create_date']):''?></td>            </tr>        <?php endforeach;?>        </tbody>        <tfoot style="background: #4abdac;    color: #fff;">            <tr style="font-size:18pt;">                <td colspan="7" class="text-right">รวมมูลค่า</td>                <td colspan="2" >฿<?= number_format($totalPrice,2)?></td>            </tr>        </tfoot>    </table></div>