<?php    use appxq\sdii\widgets\ModalForm;    $this->title = "รายงาน Bill ที่อัปโหลดจากไฟล์ Excel";?><h3 class="text-center"><?= $this->title; ?></h3><?= $this->render('_search')?><div style="background: #fff;padding: 10px;">    <?php        echo \kartik\grid\GridView::widget([            'dataProvider' => $dataProvider,            'columns' => [                [                    'headerOptions' => ['style'=>'width:50px;text-align:center;'],                    'class' => 'yii\grid\SerialColumn',                    // you may configure additional properties here                ],                [                    'format' => 'raw',                    'attribute' => 'docno',                    'value' => function ($model) {                        try {                             $data = \backend\classes\BillManager::checkBill($model->docno);                            if($data){                                if($model->net_value != $data['amount']){                                    return "                                        <div style='padding:5px;' class='kt-border-warning kt-section section-sm kt-portlet__body body-xs kt-font-warning content-border'>                                            <div class='row'>                                                <div class='col-md-8 col-xs-8 col-sm-8'>{$model->docno} <label class='label label-warning'>จำนวนเงินไม่ตรงกัน</label></div>                                                <div class='col-md-4 col-xs-4 col-sm-4 text-right'><button class='btn btn-info btnEdit' data-id='{$data['id']}'><i class='fa fa-pencil'></i> แก้ไขบิล</button></div>                                            </div>                                        </div>                                    ";                                }                                return $model->docno;                            }else{                                return "                                        <div style='padding:5px;' class='kt-border-danger kt-section section-sm kt-portlet__body body-xs kt-font-danger content-border'>                                            <div class='row'>                                                <div class='col-md-8 col-xs-8 col-sm-8'>{$model->docno}</div>                                                <div class='col-md-4 col-xs-4 col-sm-4 text-right'><button class='btn btn-success btnAdd'><i class='fa fa-plus'></i> เพิ่มบิล</button></div>                                            </div>                                        </div>                                    ";                            }                        } catch (\yii\db\Exception $ex) {                        }                    }                ],                [                    'attribute' => 'docdate',                    'contentOptions' => ['style' => 'width:100px'],                    'value' => function ($model) {                        if ($model->docdate) {                            return \appxq\sdii\utils\SDdate::mysql2phpDate($model->docdate);                        }                    }                ],                [                    'format' => 'raw',                    'contentOptions' => ['style' => 'width:100px'],                    'attribute'=>'net_value',                    'value'=>function($model){                        if($model->net_value != ''){                            return number_format($model->net_value, 2);                        }                    }                ],                [                    'format' => 'raw',                    'contentOptions' => ['style' => 'width:100px'],                        'label'=>'รายการสินค้า',                        'value'=>function($model){                            return "<button data-docno='{$model->docno}' class='btn btn-default btnViewProduct'>ดูรายการสินค้า</button>";                        }                ]            ]        ]);         ?></div><?=    ModalForm::widget([        'id' => 'modal-bill-items',        'options' => ['tabindex' => false],        'size' => 'modal-lg',        //         'clientOptions' => ['backdrop' => 'static', 'keyboard' => false]    ]);?><?php \richardfan\widget\JSRegister::begin(); ?><script>    $(".btnEdit").on('click', function(){        let id = $(this).attr('data-id');        let url = '<?= \yii\helpers\Url::to(['/bill-items/update?id='])?>'+id;        modalBillItem(url);        return false;    });    $(".btnAdd").on('click', function(){        let url = '<?= \yii\helpers\Url::to(['/bill-items/create'])?>';        modalBillItem(url);        return false;    });    $(".btnViewProduct").on('click', function(){        let id = $(this).attr('data-docno');        let url = '<?= \yii\helpers\Url::to(['/report/sell-product-by-docno?docno='])?>'+id;        modalBillItem(url);        return false;    });    function modalBillItem(url) {        $('.modal').css('overflow', 'scroll');        $('#modal-bill-items .modal-content').html('<div class=\"sdloader \"><i class=\"sdloader-icon\"></i></div>');        $('#modal-bill-items').modal('show')            .find('.modal-content')            .load(url);    }    $("#btnProcess").on('click', function () {        let stdate = $("#stdate").val();        let endate = $("#endate").val();        let url = '<?= \yii\helpers\Url::to(['/report/sell-bill'])?>';        url = `${url}?stdate=${stdate}&endate=${endate}`;        location.href=url;        return false;    });</script><?php \richardfan\widget\JSRegister::end(); ?>